export const bubbleSort = array => {

    let temp = 0;
    const animation = [];
    //const arrayBars = document.getElementsByClassName('array-bar');

    
    for(let i = 0; i<array.length; i++){
        for(let j = 0; j<array.length-i-1; j++){

            if(array[j] > array[j+1]){

                temp = array[j];
                array[j] = array[j+1];
                array[j+1] = temp;

                animation.push({indexOne : j, indexTwo : j+1, isSwap : true});
      
            }else{
                animation.push({indexOne : j+1, indexTwo : j+1, isSwap : false});
            }
        }
        
    }
    
    return animation;
}

export const quickSort = (array, animation, copiedStateArray) => {
        
        if (array.length <= 1) {
          
          animation.push({pivot : 0, nextElement : 0});
          return array;
        }
        //Set pointer
        var pivot = array[0];
        var pivotIndex = copiedStateArray.indexOf(pivot);
        
        var left = []; 
        var right = [];
        
        for (var i = 1; i < array.length; i++) {
          //Iterate through all elements
          //If element is smaller than pointer set to left
          //If element is greater than pointer set to right  
          animation.push({pivot : 0, nextElementIndex : i});
          array[i] < pivot ? left.push(array[i]) : right.push(array[i]);
        }
        
        
        //recurse left partition, right partition, then combine
        //console.log(animation);
        left = quickSort(left, animation, copiedStateArray);
        right = quickSort(right, animation, copiedStateArray);
        
        
        return animation;
//        return quickSort(left, animation).concat(pivot, quickSort((right), animation));
      
}
