import React from 'react'

import * as sortingAlgorithms from '../sortingAlgorithms/sortingAlgorithms';



export default class SortingVisualizer extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            array: [],
            intervalId: null,
            iteration: 0,
            arraySize: 75,
            animationSpeed: 0,

        }
    }
    componentDidMount(){
        this.generateArray();
    }

    //Create values for array upon mounting component
    generateArray(){
        const newArray = [];
        for(let i=0; i< this.state.arraySize; i++){
            
            newArray.push({value: randomNumberInRange(5,750), backgroundImage : 'linear-gradient(rgb(36, 117, 209), rgb(122, 37, 187))'});
        }
        this.setState({array : newArray, iteration: 0, animationSpeed : 200});
    }

    sortCompleteAnimation(){
        let i = 0;

        const intervalTwo = window.setInterval(() =>{
            
            if(i < this.state.array.length){
                this.state.array[i].backgroundImage = 'linear-gradient(rgb(239, 142, 44), rgb(232, 129, 39))';
                
            }else{
                clearInterval(intervalTwo);
            }
            this.setState({array: this.state.array});
            i++;

        },10 );
            setTimeout(()=>{
                for(let i = 0; i < this.state.array.length; i++){
                    this.state.array[i].backgroundImage = 'linear-gradient(rgb(36, 117, 209), rgb(122, 37, 187))';
                }
                this.setState({array: this.state.array});
            }, 2000);

        }
    quickSort(){
        
        let animation = [];
        let testArray = sortingAlgorithms.quickSort(this.state.array.map(function(e){return e.value}), 0, this.state.array.length-1, animation);
        
        console.log(testArray);
        

        this.setState({iteration : 0});

        // const intervalQuickSort = window.setInterval(()=>{
            
        //     //1) Get previous pivot and element colors and reset style
        //     if(this.state.iteration > 0){
        //         // let previousSwap = animation[this.state.iteration-1];
        //         // this.state.array[previousSwap.pivot].color = 'cornflowerblue';
        //         // this.state.array[previousSwap.comparedElement].color = 'cornflowerblue';
        //     }

        //     //2) Highlight pivot and compared element

        //     //3) Swap if isSwap is true


        //    //End animation
        //     if(this.state.iteration >= animation.length-1){
        //         this.sortCompleteAnimation();
        //         clearInterval(intervalQuickSort);
        //     }

        //     this.state.iteration++;

        //     this.setState({array: this.state.array, iteration : this.state.iteration});
        // }, this.state.animationSpeed);

        
    }
    
    bubbleSort(){
        

        const animation = sortingAlgorithms.bubbleSort(this.state.array.map(function(e){return e.value}));
        this.state.iteration = 0;
        //Check if array is already sorted
        let splitArray = this.state.array.map(function(e){return e.value});
        const jsSortedArray = sort(splitArray);
        //
        
        const intervalId = window.setInterval(() =>{
            
            //1) Get two previous swaps from anim array
            //2) Reset style
            
            if(this.state.iteration > 0){
                let prevSwap = animation[this.state.iteration-1];
                let copiedArray = this.state.array;
                copiedArray[prevSwap.indexOne].backgroundImage = 'linear-gradient(rgb(36, 117, 209), rgb(122, 37, 187))';
                copiedArray[prevSwap.indexTwo].backgroundImage = 'linear-gradient(rgb(36, 117, 209), rgb(122, 37, 187))';
                this.setState({array : copiedArray});
                // this.state.array[prevSwap.indexOne].backgroundImage = 'linear-gradient(rgb(36, 117, 209), rgb(122, 37, 187))';
                // this.state.array[prevSwap.indexTwo].backgroundImage = 'linear-gradient(rgb(36, 117, 209), rgb(122, 37, 187))';
            }
            
            //3) Get two current swaps
            //4) Set style
            let currentSwap = animation[this.state.iteration];
            
            if(currentSwap.isSwap === true){
                
                let temp = this.state.array[currentSwap.indexOne].value;
                this.state.array[currentSwap.indexOne].value = this.state.array[currentSwap.indexTwo].value;
                this.state.array[currentSwap.indexTwo].value = temp;
            }
            
            this.state.array[currentSwap.indexOne].backgroundImage = 'linear-gradient(rgb(255, 0, 0), rgb(255, 0, 0))';
            this.state.array[currentSwap.indexTwo].backgroundImage = 'linear-gradient(rgb(255, 0, 0), rgb(255, 0, 0))';

            //5) Check to see if last iteration then remove interval
            if(this.state.iteration >= animation.length-1){
                this.sortCompleteAnimation();
                clearInterval(intervalId);
            }

            //6) increment iterator++
            this.state.iteration++;
            
            this.setState({array: this.state.array, iteration : this.state.iteration });

        
            }, this.state.animationSpeed);
            
              

        
    }

    mergeSort(){

    }

    heapSort(){

