import React from 'react'

import * as sortingAlgorithms from '../sortingAlgorithms/sortingAlgorithms';

export default class SortingVisualizer extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            array: [],
        }
    }
    componentDidMount(){
        this.generateArray();
    }

    //Create values for array upon mounting component
    generateArray(){
        const array = [];
        for(let i=0; i< 55; i++){
            
            array.push(randomNumberInRange(5,750));
        }
        this.setState({array});
    }

    quickSort(){
        
    }

    bubbleSort(){
        //sortingAlgorithms.bubbleSort(this.state.array);
        

        const animation = sortingAlgorithms.bubbleSort(this.state.array);
        const bars = document.getElementsByClassName('array-bar');
        
        let barPointer = 0;
        const barOneStyle = bars[barPointer].style;
        const barTwoStyle = bars[barPointer+1].style;
       
        for(let i=0; i<animation.length; i++){
            
            
            if(barPointer < this.state.array.length-1){
                
                barOneStyle = bars[barPointer].style;
                barTwoStyle = bars[barPointer+1].style;
            }else{
                barTwoStyle = bars[barPointer].style;
            }
            
            
            
            let setHeight = animation[i];
            let setHeight2 = animation[i+1];
            //Add a check to see if swap occurs in same animation frame
            
            if(barPointer === this.state.array.length-1){
                barPointer = 0;
            }
            setTimeout(() => {
                barOneStyle.backgroundColor = 'red';
                barTwoStyle.backgroundColor = 'red';
                barOneStyle.height = `${setHeight}px`;
                barTwoStyle.height = `${setHeight2}px`;

            },i*30);
            setTimeout(() => {
                barOneStyle.backgroundColor = 'cornflowerblue';
                barTwoStyle.backgroundColor = 'cornflowerblue';
                    

            },i*20);
            if(animation[i] < animation[i+1] ){
                barPointer++;
            }
            
            

        }
    }

    mergeSort(){

    }

    heapSort(){

    }

    //Manually change sorting method to test
    testSortAlgorithm(){
        for(let i = 0; i<40; i++){
            const array = [];
            for(let i=0; i< 55; i++){
                
                array.push(randomNumberInRange(5,750));
            }
            //Sorting algorithm array
            const sortedArray = sortingAlgorithms.bubbleSort(array);
            //JS sorted array
            const jsSortedArray = array.sort();
            console.log(arrayEquals(sortedArray, jsSortedArray));
            
        }
    
        
    }

    // For each value in the array map it to a div-array-bar with a key id
    render(){
        const {array} = this.state;
        
        return(
            
            <div className='array-container'>
            {array.map((value, idx) => (
                
                <div className='array-bar' style={{height: `${value}px`}} key={idx}>
                    
                </div>
                
            ))};
            <button onClick={() => this.generateArray()}>Generate New Array</button>
            <button onClick={() => this.quickSort()}>Quick Sort</button>
            <button onClick={() => this.bubbleSort()}>Bubble Sort</button>
            <button onClick={() => this.generateArray()}>Merge Sort</button>
            <button onClick={() => this.generateArray()}>Heap Sort</button>
            <button onClick={() => this.testSortAlgorithm()}>Test Algorithm</button>
            
            </div>
           
        );
       
    }
 
}
function randomNumberInRange(min, max) {
    
    return Math.floor(Math.random() * (max - min + 1)) + min;
}
function arrayEquals(arrayOne, arrayTwo){
    if(arrayOne.length !== arrayTwo.length){
        return false;
    }else {
        for(let i = 0; i<arrayOne.length; i++){
            if(arrayOne[i] !== arrayTwo[i]){
                return false;
            }
        }
    }
    return true;
}
