import React from 'react'

export default class SortingVisualizer extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            array: [],
        }
    }
    loadComponent(){
        this.resetArray();
    }

    resetArray(){
        const array = [];
        for(let i=0; i< 20; i++){
            //RandomIntFromInterval(min,max)
            array.push(Math.random()*50);
        }
        this.setState({array});
    }

    render(){
        const {array} = this.state;
        <>
        return(
            {array.map((value, idx) => (
                <div className='array-bar' key={idx}>
                    {value}
                </div>
            ))}
        );
        </>
    }
    
    
}
