import React from 'react'

import * as sortingAlgorithms from '../sortingAlgorithms/sortingAlgorithms';



export default class SortingVisualizer extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            array: [],
            intervalId: null,
            iteration: 0,
            animation: [],
        }
    }
    componentDidMount(){
        this.generateArray();
    }

    //Create values for array upon mounting component
    generateArray(){
        const newArray = [];
        for(let i=0; i< 55; i++){
            
            newArray.push({value: randomNumberInRange(5,750), color : 'cornflowerblue'});
        }
        this.setState({array : newArray, iteration: 0});
    }

    sortCompleteAnimation(){
        let i = 0;

        const intervalTwo = window.setInterval(() =>{
            
            if(i < this.state.array.length){
                this.state.array[i].color = 'orange';
                
            }else{
                clearInterval(intervalTwo);
            }
            this.setState({array: this.state.array});
            i++;

        },10 );
            setTimeout(()=>{
                for(let i = 0; i < this.state.array.length; i++){
                    this.state.array[i].color = 'cornflowerblue';
                }
                this.setState({array: this.state.array});
            }, 2000);

        }
    quickSort(){
        const newArray = [];
        const animation = sortingAlgorithms.quickSort(this.state.array.map(function(e){return e.value}), newArray);
        for(let i = 0; i < this.state.array.length; i++){
            //this.state.array[i].value = sortedArray[i];
        }
        console.log(animation);

        this.state.iteration = 0;

        const intervalQuickSort = window.setInterval(()=>{
            let currentPosition = animation[this.state.iteration];
            if(currentPosition.pivot != null){
                this.state.array[currentPosition.pivot].color = 'red';
            }
            
            if(this.state.iteration >= animation.length-1){
                this.sortCompleteAnimation();
                clearInterval(intervalQuickSort);
            }

            this.state.iteration++;

            this.setState({array: this.state.array});
        }, 10);

        
    }
    
    bubbleSort(){
        

        const animation = sortingAlgorithms.bubbleSort(this.state.array.map(function(e){return e.value}));
        this.state.iteration = 0;
        //Check if array is already sorted
        let splitArray = this.state.array.map(function(e){return e.value});
        const jsSortedArray = sort(splitArray);
        //
        
        const intervalId = window.setInterval(() =>{
            
            //1) Get two previous swaps from anim array
            //2) Reset style
            
            if(this.state.iteration > 0){
                let prevSwap = animation[this.state.iteration-1];
                this.state.array[prevSwap.indexOne].color = 'cornflowerblue';
                this.state.array[prevSwap.indexTwo].color = 'cornflowerblue';
            }
            
            //3) Get two current swaps
            //4) Set style
            let currentSwap = animation[this.state.iteration];
            
            if(currentSwap.isSwap === true){
                
                let temp = this.state.array[currentSwap.indexOne].value;
                this.state.array[currentSwap.indexOne].value = this.state.array[currentSwap.indexTwo].value;
                this.state.array[currentSwap.indexTwo].value = temp;
            }
            
            this.state.array[currentSwap.indexOne].backgroundColor = 'red';
            this.state.array[currentSwap.indexTwo].color = 'red';

            //5) Check to see if last iteration then remove interval
            if(this.state.iteration >= animation.length-1){
                this.sortCompleteAnimation();
                clearInterval(intervalId);
            }

            //6) increment iterator++
            this.state.iteration++;
            
            this.setState({array: this.state.array, iteration : this.state.iteration });

        
            }, 2);
            
              

        
    }

    mergeSort(){

    }

    heapSort(){

    }

    //Manually change sorting method to test
    testSortAlgorithm(){
        for(let i = 0; i<40; i++){
            const testedArray = [];
            for(let i=0; i< 55; i++){
                
                testedArray.push(randomNumberInRange(5,750));
            }
            //Sorting algorithm array
            const sortedArray = sortingAlgorithms.bubbleSort(testedArray);
            //JS sorted array
            const jsSortedArray = testedArray.sort();
            console.log(arrayEquals(sortedArray, jsSortedArray));
            
        }
    
        
    }

    // For each value in the array map it to a div-array-bar with a key id
    render(){
        const {array} = this.state;
        
        return(
            
            <div className='array-container'>
            {array.map((value, idx) => (
                
                <div className='array-bar' style={{height: `${value.value}px`, backgroundColor : `${value.color}`}} key={idx}>
                    
                </div>
                
            ))};
            <button onClick={() => this.generateArray()}>Generate New Array</button>
            <button onClick={() => this.quickSort()}>Quick Sort</button>
            <button onClick={() => this.bubbleSort()}>Bubble Sort</button>
            <button onClick={() => this.generateArray()}>Merge Sort</button>
            <button onClick={() => this.generateArray()}>Heap Sort</button>
            <button onClick={() => this.testSortAlgorithm()}>Test Algorithm</button>
            
            </div>
           
        );
       
    }
 
}
function randomNumberInRange(min, max) {
    
    return Math.floor(Math.random() * (max - min + 1)) + min;
}
function arrayEquals(arrayOne, arrayTwo){
    if(arrayOne.length !== arrayTwo.length){
        return false;
    }else {
        for(let i = 0; i<arrayOne.length; i++){
            if(arrayOne[i] != arrayTwo[i]){
                return false;
            }
        }
    }
    return true;
}

function sort(array){
    let temp = 0;
    for(let i = 0; i<array.length; i++){
        for(let j = 0; j<array.length-i-1; j++){

            if(array[j] > array[j+1]){

                temp = array[j];
                array[j] = array[j+1];
                array[j+1] = temp;
            }
        }
        
    }
    return array;
}
