export const bubbleSort = array => {

    let temp = 0;
    const animation = [];
    //const arrayBars = document.getElementsByClassName('array-bar');

    
    for(let i = 0; i<array.length; i++){
        for(let j = 0; j<array.length-i-1; j++){
            console.log(array.length-i-1);
            if(array[j] > array[j+1]){

                temp = array[j];
                array[j] = array[j+1];
                array[j+1] = temp;

                animation.push({indexOne : j, indexTwo : j+1, isSwap : true, setDone: false});
      
            }else{
                animation.push({indexOne : j+1, indexTwo : j+1, isSwap : false, setDone : false});
            }
            if(array[j-1] == array.length-i-1){
                animation.push({indexOne : j+1, indexTwo : j+1, isSwap : false, setDone: true});
            }
        }
        
        
    }
    
    return animation;
}
//Pass in start position relative to step above
//Increment start value accordingly to match global space
export const quickSort = (array, animation, offset) => {
        var pivot = array[0];
        
        if (array.length <= 1) {
          
          animation.push({pivot : offset, comparedElement : offset });
          return array;
        }
    
        var left = [];
        var right = [];
        
        //Partition
        for (var i = 1; i < array.length; i++) {
          //Iterate through all elements
          //If element is smaller than pivot set to left
          //If element is greater than pivot set to right  
          if(array[i] < pivot){
            left.push(array[i]);
            animation.push({pivot: offset, comparedElement: i, isSwap : true});
          }else{
            right.push(array[i]);
            animation.push({pivot: offset, comparedElement: i, isSwap : false});
          }
        }
        console.log(offset);
        left = quickSort(left, animation, offset);
        
        right = quickSort(right, animation, (offset+left.length+1));
   
        return animation;
//        return quickSort(left, animation).concat(pivot, quickSort((right), animation));
      
}
